func day1() {

	for _, tc := range testcases {
		log.Println(calculate(tc), calculate2(tc))
	}
}

func calculate(input string) int {

	var floor int

	for _, c := range input {
		if c == '(' {
			floor++
		}
		if c == ')' {
			floor--
		}
	}
	return floor
}

func calculate2(input string) int {

	var floor int

	for i, c := range input {
		if c == '(' {
			floor++
		}
		if c == ')' {
			floor--
		}
		if floor < 0 {
			return i + 1
		}
	}
	return floor
}
